css:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: 'Arial', sans-serif;
  line-height: 1.6;
  color: #333;
}

.container {
  width: 90%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 15px;
}

/* Header Styles */
header {
  background-color: #fff;
  box-shadow: 0 2px 5px rgba(0,0,0,0.1);
  position: fixed;
  width: 100%;
  top: 0;
  z-index: 1000;
}

header .container {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 20px 15px;
}

.logo {
  font-size: 1.5rem;
  font-weight: 700;
  color: #2c3e50;
}

nav ul {
  display: flex;
  list-style: none;
}

nav ul li {
  margin-left: 20px;
}

nav ul li a {
  text-decoration: none;
  color: #2c3e50;
  font-weight: 500;
  transition: color 0.3s;
}

nav ul li a:hover {
  color: #3498db;
}

/* Hero Section */
#hero {
  height: 80vh;
  background-color: #f5f5f5;
  display: flex;
  align-items: center;
  text-align: center;
  padding-top: 80px;
}

#hero h1 {
  font-size: 3rem;
  margin-bottom: 20px;
  color: #2c3e50;
}

#hero p {
  font-size: 1.2rem;
  margin-bottom: 30px;
  color: #7f8c8d;
}

.btn {
  display: inline-block;
  padding: 12px 30px;
  border-radius: 30px;
  text-decoration: none;
  font-weight: 700;
  transition: all 0.3s;
}

.btn-primary {
  background-color: #3498db;
  color: white;
}

.btn-primary:hover {
  background-color: #2980b9;
  transform: translateY(-3px);
}

/* Services Section */
#services {
  padding: 80px 0;
  background-color: #fff;
}

#services h2 {
  text-align: center;
  margin-bottom: 50px;
  color: #2c3e50;
  font-size: 2.5rem;
}

.services-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 30px;
}

.service-card {
  padding: 30px;
  background-color: #f9f9f9;
  border-radius: 10px;
  text-align: center;
  transition: transform 0.3s;
}

.service-card:hover {
  transform: translateY(-10px);
}

.icon {
  font-size: 2rem;
  margin-bottom: 20px;
  color: #3498db;
}

.service-card h3 {
  font-size: 1.5rem;
  margin-bottom: 15px;
  color: #2c3e50;
}

/* About Section */
#about {
  padding: 80px 0;
  background-color: #f5f5f5;
}

.about-content {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 50px;
  align-items: center;
}

.about-image img {
  width: 100%;
  border-radius: 10px;
}

.about-text h2 {
  font-size: 2.5rem;
  margin-bottom: 20px;
  color: #2c3e50;
}

.about-text p {
  margin-bottom: 15px;
  color: #7f8c8d;
}

/* Contact Section */
#contact {
  padding: 80px 0;
  background-color: #fff;
}

#contact h2 {
  text-align: center;
  margin-bottom: 50px;
  color: #2c3e50;
  font-size: 2.5rem;
}

.contact-wrapper {
  display: grid;
  grid-template-columns: 1fr 2fr;
  gap: 50px;
}

.contact-info h3 {
  font-size: 1.5rem;
  margin-bottom: 20px;
  color: #2c3e50;
}

.contact-info p {
  margin-bottom: 10px;
  color: #7f8c8d;
}

.form-group {
  margin-bottom: 20px;
}

.form-group label {
  display: block;
  margin-bottom: 5px;
  color: #2c3e50;
}

.form-group input,
.form-group textarea {
  width: 100%;
  padding: 10px;
  border: 1px solid #ddd;
  border-radius: 5px;
}

/* Footer */
footer {
  background-color: #2c3e50;
  color: white;
  padding: 30px 0;
  text-align: center;
}

/* Responsive */
@media (max-width: 768px) {
  nav ul {
    display: none;
  }

  .about-content,
  .contact-wrapper {
    grid-template-columns: 1fr;
  }

  #hero {
    height: auto;
    padding: 100px 0 50px;
  }

  #hero h1 {
    font-size: 2rem;
  }
}
