css:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: 'Poppins', sans-serif;
  line-height: 1.6;
  color: #333;
  background-color: #f8f9fa;
}

.container {
  width: 90%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 15px;
}

a {
  text-decoration: none;
  color: inherit;
  transition: color 0.3s ease;
}

ul {
  list-style: none;
}

img {
  max-width: 100%;
  height: auto;
}

/* Header Styles */
.top-bar {
  background-color: #f5f5f5;
  padding: 8px 0;
  font-size: 0.9rem;
}

.top-bar .container {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.top-bar-right ul {
  display: flex;
}

.top-bar-right ul li {
  margin-left: 20px;
}

.top-bar-right ul li a:hover {
  color: #ff6b6b;
}

.main-header {
  padding: 20px 0;
  background-color: white;
}

.main-header .container {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.logo h1 {
  font-size: 2rem;
  color: #ff6b6b;
  font-weight: 700;
}

.search-bar {
  flex: 1;
  max-width: 500px;
  margin: 0 20px;
}

.search-bar form {
  display: flex;
}

.search-bar input {
  width: 100%;
  padding: 10px 15px;
  border: 1px solid #ddd;
  border-radius: 5px 0 0 5px;
  font-size: 1rem;
}

.search-bar button {
  padding: 10px 20px;
  background-color: #ff6b6b;
  color: white;
  border: none;
  border-radius: 0 5px 5px 0;
  cursor: pointer;
  transition: background-color 0.3s ease;
}

.search-bar button:hover {
  background-color: #ff5252;
}

.header-actions {
  display: flex;
  align-items: center;
}

.wishlist, .cart {
  margin-left: 20px;
  font-weight: 500;
}

.wishlist a:hover, .cart a:hover {
  color: #ff6b6b;
}

.main-nav {
  background-color: #333;
  padding: 15px 0;
}

.main-nav ul {
  display: flex;
}

.main-nav ul li {
  position: relative;
  margin-right: 30px;
}

.main-nav ul li a {
  color: white;
  font-weight: 500;
  padding: 8px 0;
}

.main-nav ul li a:hover {
  color: #ff6b6b;
}

.has-dropdown {
  position: relative;
}

.dropdown {
  position: absolute;
  top: 100%;
  left: 0;
  min-width: 200px;
  background-color: white;
  box-shadow: 0 5px 15px rgba(0,0,0,0.1);
  border-radius: 5px;
  padding: 10px 0;
  z-index: 100;
  display: none;
}

.has-dropdown:hover .dropdown {
  display: block;
}

.dropdown ul li {
  margin: 0;
}

.dropdown ul li a {
  color: #333;
  padding: 8px 20px;
  display: block;
}

.dropdown ul li a:hover {
  background-color: #f5f5f5;
  color: #ff6b6b;
}

/* Hero Slider */
.hero-slider {
  background-image: url('slider-bg.jpg');
  background-size: cover;
  background-position: center;
  color: white;
  padding: 80px 0;
  text-align: center;
}

.slider-content {
  max-width: 600px;
  margin: 0 auto;
}

.slider-content h2 {
  font-size: 3rem;
  margin-bottom: 20px;
}

.slider-content p {
  font-size: 1.2rem;
  margin-bottom: 30px;
}

/* Buttons */
.btn {
  display: inline-block;
  padding: 12px 30px;
  border-radius: 5px;
  font-weight: 600;
  transition: all 0.3s ease;
  cursor: pointer;
}

.btn-primary {
  background-color: #ff6b6b;
  color: white;
}

.btn-primary:hover {
  background-color: #ff5252;
}

.btn-secondary {
  background-color: #333;
  color: white;
}

.btn-secondary:hover {
  background-color: #444;
}

.btn-outline {
  background-color: transparent;
  border: 2px solid #ff6b6b;
  color: #ff6b6b;
}

.btn-outline:hover {
  background-color: #ff6b6b;
  color: white;
}

.btn-light {
  background-color: white;
  color: #333;
}

.btn-light:hover {
  background-color: #f5f5f5;
}

/* Features */
.features {
  padding: 50px 0;
  background-color: white;
  border-bottom: 1px solid #eee;
}

.features .container {
  display: flex;
  justify-content: space-between;
  flex-wrap: wrap;
}

.feature-box {
  flex: 1;
  min-width: 250px;
  display: flex;
  align-items: center;
  padding: 20px;
}

.feature-icon {
  font-size: 2rem;
  color: #ff6b6b;
  margin-right: 15px;
}

.feature-text h3 {
  font-size: 1.2rem;
  margin-bottom: 5px;
}

.feature-text p {
  color: #666;
  font-size: 0.9rem;
}

/* Sections */
section {
  padding: 80px 0;
}

.section-header {
  text-align: center;
  margin-bottom: 50px;
}

.section-header h2 {
  font-size: 2.5rem;
  margin-bottom: 10px;
  color: #333;
}

.section-header p {
  color: #666;
  font-size: 1.1rem;
}

/* Categories */
.category-grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
  gap: 30px;
}

.category-card {
  position: relative;
  border-radius: 10px;
  overflow: hidden;
  box-shadow: 0 5px 15px rgba(0,0,0,0.1);
  text-align: center;
}

.category-card img {
  width: 100%;
  height: 200px;
  object-fit: cover;
  transition: transform 0.5s ease;
}

.category-card:hover img {
  transform: scale(1.1);
}

.category-card h3 {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -80px);
  color: white;
  font-size: 1.5rem;
  z-index: 1;
  text-shadow: 1px 1px 3px rgba(0,0,0,0.3);
}

.category-card .btn {
  position: absolute;
  bottom: 20px;
  left: 50%;
  transform: translateX(-50%);
  z-index: 1;
}

.category-card::after {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(to bottom, rgba(0,0,0,0.2), rgba(0,0,0,0.6));
}

/* Products */
.product-grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
  gap: 30px;
}

.product-card {
  background-color: white;
  border-radius: 10px;
  box-shadow: 0 5px 15px rgba(0,0,0,0.05);
  overflow: hidden;
  transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.product-card:hover {
  transform: translateY(-5px);
  box-shadow: 0 10px 20px rgba(0,0,0,0.1);
}

.product-badges {
  position: absolute;
  top: 10px;
  left: 10px;
  z-index: 1;
}

.badge {
  display: inline-block;
  padding: 5px 10px;
  border-radius: 3px;
  color: white;
  font-size: 0.8rem;
  font-weight: 600;
  margin-right: 5px;
}

.badge-new {
  background-color: #4caf50;
}

.badge-sale {
  background-color: #ff6b6b;
}

.badge-hot {
  background-color: #ff9800;
}

.product-thumb {
  position: relative;
  height: 250px;
  overflow: hidden;
}

.product-thumb img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.5s ease;
}

.product-card:hover .product-thumb img {
  transform: scale(1.1);
}

.product-actions {
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  padding: 15px;
  background-color: rgba(255,255,255,0.9);
  display: flex;
  justify-content: center;
  transform: translateY(100%);
  transition: transform 0.3s ease;
}

.product-card:hover .product-actions {
  transform: translateY(0);
}

.product-actions button {
  background: none;
  border: none;
  margin: 0 5px;
  padding: 5px 10px;
  font-size: 0.9rem;
  color: #333;
  cursor: pointer;
  transition: color 0.3s ease;
}

.product-actions button:hover {
  color: #ff6b6b;
}

.product-info {
  padding: 20px;
}

.product-title {
  font-size: 1.1rem;
  margin-bottom: 10px;
  font-weight: 500;
}

.product-price {
  margin-bottom: 10px;
}

.price {
  font-size: 1.2rem;
  font-weight: 600;
  color: #ff6b6b;
}

.old-price {
  text-decoration: line-through;
  color: #999;
  margin-right: 10px;
}

.product-rating {
  color: #ffc107;
}

.rating-count {
  color: #999;
  font-size: 0.9rem;
}

.view-all {
  text-align: center;
  margin-top: 50px;
}

/* Banner */
.banner {
  background-image: url('banner-bg.jpg');
  background-size: cover;
  background-position: center;
  color: white;
  text-align: center;
  padding: 100px 0;
}

.banner-content {
  max-width: 600px;
  margin: 0 auto;
}

.banner-content h2 {
  font-size: 3rem;
  margin-bottom: 20px;
}

.banner-content p {
  font-size: 1.2rem;
  margin-bottom: 30px;
}

/* Newsletter */
.newsletter {
  background-color: #f8f9fa;
  text-align: center;
}

.newsletter-content {
  max-width: 600px;
  margin: 0 auto;
}

.newsletter-content h2 {
  font-size: 2rem;
  margin-bottom: 10px;
}

.newsletter-content p {
  margin-bottom: 30px;
  color: #666;
}

.newsletter-form {
  display: flex;
  max-width: 500px;
  margin: 0 auto;
}

.newsletter-form input {
  flex: 1;
  padding: 12px 15px;
  border: 1px solid #ddd;
  border-radius: 5px 0 0 5px;
  font-size: 1rem;
}

.newsletter-form button {
  padding: 12px 20px;
  background-color: #ff6b6b;
  color: white;
  border: none;
  border-radius: 0 5px 5px 0;
  cursor: pointer;
  transition: background-color 0.3s ease;
}

.newsletter-form button:hover {
  background-color: #ff5252;
}

/* Footer */
footer {
  background-color: #333;
  color: white;
  padding-top: 80px;
}

.footer-top .container {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 30px;
}

.footer-widget h3 {
  font-size: 1.5rem;
  margin-bottom: 20px;
  position: relative;
  padding-bottom: 10px;
}

.footer-widget h3::after {
  content: '';
  position: absolute;
  left: 0;
  bottom: 0;
  width: 50px;
  height: 2px;
  background-color: #ff6b6b;
}

.footer-widget p {
  color: #aaa;
  margin-bottom: 15px;
}

.footer-widget ul li {
  margin-bottom: 10px;
}

.footer-widget ul li a:hover {
  color: #ff6b6b;
}

.social-links {
  display: flex;
  gap: 10px;
  margin-top: 20px;
}

.social-links a {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 40px;
  height: 40px;
  background-color: rgba(255,255,255,0.1);
  border-radius: 50%;
  transition: background-color 0.3s ease;
}

.social-links a:hover {
  background-color: #ff6b6b;
}

.footer-bottom {
  background-color: #222;
  padding: 20px 0;
  margin-top: 60px;
}

.footer-bottom .container {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.payment-methods {
  display: flex;
  gap: 10px;
}

.payment-methods span {
  background-color: rgba(255,255,255,0.1);
  padding: 5px 10px;
  border-radius: 3px;
  font-size: 0.8rem;
}

/* Responsive */
@media (max-width: 992px) {
  .main-header .container {
    flex-wrap: wrap;
  }

  .logo {
    margin-bottom: 20px;
  }

  .search-bar {
    order: 3;
    margin: 20px 0 0;
    max-width: 100%;
  }
}

@media (max-width: 768px) {
  .main-nav ul {
    flex-direction: column;
  }

  .main-nav ul li {
    margin-right: 0;
    margin-bottom: 10px;
  }

  .dropdown {
    position: static;
    display: none;
    width: 100%;
    box-shadow: none;
    padding: 0;
    margin-top: 10px;
  }

  .features .container {
    flex-direction: column;
  }

  .feature-box {
    margin-bottom: 20px;
  }

  .footer-bottom .container {
    flex-direction: column;
    text-align: center;
  }

  .footer-bottom p {
    margin-bottom: 20px;
  }
}

@media (max-width: 576px) {
  .top-bar .container {
    flex-direction: column;
    text-align: center;
  }

  .top-bar-right ul {
    margin-top: 10px;
    justify-content: center;
  }

  .slider-content h2,
  .banner-content h2 {
    font-size: 2.2rem;
  }

  .newsletter-form {
    flex-direction: column;
  }

  .newsletter-form input {
    border-radius: 5px;
    margin-bottom: 10px;
  }

  .newsletter-form button {
    border-radius: 5px;
  }
}
